package org.apache.kafka.common.metrics.riemann;

import com.aphyr.riemann.Proto;
import org.apache.kafka.common.metrics.KafkaMetric;
import org.apache.kafka.common.metrics.MetricsReporter;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.IOException;
import java.net.InetAddress;
import java.net.UnknownHostException;
import java.util.List;
import java.util.Map;

/***
 Created by nitish.goyal on 05/11/19
 ***/
public class RiemannReporter implements MetricsReporter {

    private static final Logger LOGGER = LoggerFactory.getLogger(RiemannReporter.class);
    private RiemannEventPublisher publisher;

    @Override
    public void init(List<KafkaMetric> metrics) {
        try {
            LOGGER.info("Initializing riemann reporter");
            publisher = RiemannTcpClientPublisher.buildFromProperties();
        }
        catch (Exception e) {
            LOGGER.error("Error occurred while initialising Riemann reported", e);
        }

    }

    @Override
    public void metricChange(KafkaMetric metric) {
        Proto.Event.Builder builder = buildEvent(metric);
        if(metric.metricValue() instanceof Double && (Double) metric.metricValue() > 0d) {
            LOGGER.info(String.format("Metric %s changed with value : %s ",
                                      metric.metricName().group(),
                                      metric.metricValue()));
            Proto.Event event = builder.setMetricD((Double) metric.metricValue())
                    .addAttributes(buildMetricTypeAttribute("meter"))
                    .build();
            try {
                sendEvent(event);
            }
            catch (Exception e) {
                LOGGER.error("Error occurred while sending event to riemann : ", e);
            }

package org.apache.kafka.common.metrics.riemann;

import com.aphyr.riemann.Proto;
import com.aphyr.riemann.client.RiemannClient;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.IOException;

public class RiemannTcpClientPublisher implements RiemannEventPublisher {

    private static final Logger LOGGER = LoggerFactory.getLogger(RiemannTcpClientPublisher.class);

    private final RiemannClient riemann;

    public RiemannTcpClientPublisher(RiemannClient riemann) {
        this.riemann = riemann;
    }

    @Override
    public void publish(Proto.Event event) throws IOException {
        riemann.sendEventsWithAck(event);
        LOGGER.info("Event successfully published");
    }

    private void connect() throws IOException {
        this.riemann.connect();
    }

    public static RiemannTcpClientPublisher buildFromProperties() throws IOException {
        //String host = props.getString("kafka.riemann.metrics.reporter.publisher.host", "127.0.0.1");
        //Integer port = props.getInt("kafka.riemann.metrics.reporter.publisher.port", 5555);
        String host = "prd-riemannapi001.phonepe.nm5";
        Integer port = 5555;

        RiemannTcpClientPublisher publisher = new RiemannTcpClientPublisher(RiemannClient.tcp(host, port));
        publisher.connect();

        LOGGER.info("Connection established to the host");
        return publisher;
    }
}
